class Aula_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get aula by aula_id
     */
    function get_aula($aula_id)
    {
        $aula = $this->db->query("
            SELECT
                *

            FROM
                `aula`

            WHERE
                `aula_id` = ?
        ",array($aula_id))->row_array();

        return $aula;
    }
        
    /*
     * Get all aula
     */
    function get_all_aula()
    {
        $aula = $this->db->query("
            SELECT
                au.*, ta.*

            FROM
                aula au, tipo_aula ta

            WHERE
                au.aula_tipo = ta.tipoaula_id

            ORDER BY `aula_id` DESC
        ")->result_array();

        return $aula;
    }
        
    /*
     * function to add new aula
     */
    function add_aula($params)
    {
        $this->db->insert('aula',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update aula
     */
    function update_aula($aula_id,$params)
    {
        $this->db->where('aula_id',$aula_id);
        return $this->db->update('aula',$params);
    }
    
    /*
     * function to delete aula
     */
    function delete_aula($aula_id)
    {
        return $this->db->delete('aula',array('aula_id'=>$aula_id));
    }
}
