    /*
     * Get horario by horario_id
     */
    function get_horario($horario_id)
    {
        $horario = $this->db->query("
            SELECT
                *

            FROM
                `horario`

            WHERE
                `horario_id` = ?
        ",array($horario_id))->row_array();

        return $horario;
    }

    public function get_horario2($horario_id)
    {
        $this->db->select('*');
        $this->db->from('horario');
        $this->db->join('periodo', 'horario.periodo_id = periodo.periodo_id');
