    /*
     * Get all horario count
     */
    function get_all_horario_count()
    {
        $horario = $this->db->query("
            SELECT
                count(*) as count

            FROM
                `horario`
        ")->row_array();

        return $horario['count'];
    }
        
    /*
     * Get all horario
     */
    function get_all_horario($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $horario = $this->db->query("
            SELECT  * FROM  `horario`
            WHERE  1 = 1
            ORDER BY `horario_id` DESC

            " . $limit_condition . "
        ")->result_array();

        return $horario;
    }

    public function get_all_horarios($params=array())
    {
        $limit_condition = "";

        $this->db->select('*');
        $this->db->from('horario');
        $this->db->join('periodo', 'horario.periodo_id = periodo.periodo_id');
        $this->db->order_by("horario_id", "ASC");

        if(isset($params) && !empty($params)){
            $this->db->limit($params['limit'], $params['offset']);
        }

        $query = $this->db->get();
        return $query->result();
    }


        
    /*
