        return $this->db->update('horario',$params);
    }
    
    /*
     * function to delete horario
     */
    function delete_horario($horario_id)
    {
        return $this->db->delete('horario',array('horario_id'=>$horario_id));
    }
    
    /* muestra todos los horarios Activos */
    function get_all_horarios_activos()
    {
        $horario = $this->db->query("
            SELECT
                h.*

            FROM
                horario h

            WHERE
                1=1

            ORDER BY h.horario_id asc
        ")->result_array();

        return $horario;
    }
    /*  */
    function existe_horario($aula, $periodo, $dia)
    {
        $horario = $this->db->query("
            SELECT
                count(h.horario_id) as res
                
            FROM
                horario h
            where
                h.aula_id = $aula
                and h.periodo_id = $periodo
                and h.dia_id = $dia
        ")->row_array();

        return $horario;
    }

