                `horario`
        ")->row_array();

        return $horario['count'];
    }
        
    /*
     * Get all horario
     */
    function get_all_horario($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $horario = $this->db->query("
            SELECT
                *

            FROM
                `horario`

            WHERE
                1 = 1

            ORDER BY `horario_id` DESC

            " . $limit_condition . "
        ")->result_array();

        return $horario;
    }
        
