class Materia_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get materia by materia_id
     */
    function get_materia($materia_id)
    {
        $materia = $this->db->query("
            SELECT
                *

            FROM
                `materia`

            WHERE
                `materia_id` = ?
        ",array($materia_id))->row_array();

        return $materia;
    }
        
    /*
     * Get all materia
     */
    function get_all_materia()
    {
        $materia = $this->db->query("
            SELECT
                *

            FROM
                `materia`

            WHERE
                1 = 1

            ORDER BY `materia_id` DESC
        ")->result_array();

        return $materia;
    }
        
    /*
     * function to add new materia
     */
    function add_materia($params)
    {
        $this->db->insert('materia',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update materia
     */
    function update_materia($materia_id,$params)
    {
        $this->db->where('materia_id',$materia_id);
        return $this->db->update('materia',$params);
    }
    
    /*
     * function to delete materia
     */
    function delete_materia($materia_id)
    {
        return $this->db->delete('materia',array('materia_id'=>$materia_id));
    }
    /*
     * Get all materia de un NIVEL
     */
    function get_all_materia_nivel($nivel_id)
    {
        $materia = $this->db->query("
            SELECT
                m.materia_id, m.materia_nombre, m.materia_codigo

            FROM
                materia m

            WHERE
                m.nivel_id = $nivel_id

            ORDER BY `materia_id` ASC
        ")->result_array();

        return $materia;
    }
    /* ****** Get all materias activos***** */
    function get_all_materias_activo()
    {
        $materia = $this->db->query("
            SELECT
                m.materia_id, m.materia_nombre, m.materia_codigo

            FROM
                materia m

            WHERE
                m.estado_id = 1

            ORDER BY `materia_id` ASC
        ")->result_array();

        return $materia;
    }
}
