class Materia_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get materia by materia_id
     */
    function get_materia($materia_id)
    {
        $materia = $this->db->query("
            SELECT
                *

            FROM
                `materia`

            WHERE
                `materia_id` = ?
        ",array($materia_id))->row_array();

        return $materia;
    }
        
    /*
     * Get all materia
     */
    function get_all_materia()
    {
        $materia = $this->db->query("
            SELECT
                m.*, a.*, n.*, e.*, t.materia_nombre as 'requisito'

            FROM
                materia m
            LEFT JOIN area_materia a ON m.area_id=a.area_id
            LEFT JOIN nivel n ON m.nivel_id=n.nivel_id
            LEFT JOIN estado e ON m.estado_id=e.estado_id
            LEFT JOIN materia t ON m.mat_materia_id=t.materia_id

            WHERE
                1 = 1

            ORDER BY `materia_id` DESC
        ")->result_array();

        return $materia;
    }
        
    /*
     * function to add new materia
     */
    function add_materia($params)
    {
        $this->db->insert('materia',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update materia
     */
    function update_materia($materia_id,$params)
    {
        $this->db->where('materia_id',$materia_id);
        return $this->db->update('materia',$params);
    }
    
    /*
     * function to delete materia
     */
    function delete_materia($materia_id)
    {
        return $this->db->delete('materia',array('materia_id'=>$materia_id));
    }
    /*
     * Get all materia de un NIVEL
     */
    function get_all_materia_nivel($nivel_id)
    {
        $materia = $this->db->query("
            SELECT
                m.materia_id, m.materia_nombre, m.materia_codigo, m.materia_horas,
                c.carrera_modalidad,
                pr.mat_materia_id, pr.materia_codigo as cod
            FROM
                materia m
            LEFT JOIN materia pr on m.materia_id = pr.materia_id
            LEFT JOIN nivel n on m.nivel_id = n.nivel_id
            LEFT JOIN plan_academico pa on n.planacad_id = pa.planacad_id
            LEFT JOIN carrera c on pa.carrera_id = c.carrera_id
            where m.nivel_id = $nivel_id
            
            GROUP BY m.materia_id

            /*SELECT
                m.materia_id, m.materia_nombre, m.materia_codigo, m.materia_horas, c.carrera_modalidad

            FROM
                materia m, nivel n, plan_academico pa, carrera c

            WHERE
                m.nivel_id = $nivel_id
                and m.nivel_id = n.nivel_id
                and n.planacad_id = pa.planacad_id
                and pa.carrera_id = c.carrera_id
            
            ORDER BY m.materia_id ASC */
        ")->result_array();

        return $materia;
    }
    /* ****** Get all materias activos***** */
    function get_all_materias_activo_plan($planacad_id)
    {
        $materia = $this->db->query("
            SELECT
                m.materia_id, m.materia_nombre, m.materia_codigo

            FROM
                materia m, nivel n

            WHERE
                m.estado_id = 1
                and  m.nivel_id = n.nivel_id
                and n.planacad_id = $planacad_id

            ORDER BY `materia_id` ASC
        ")->result_array();

        return $materia;
    }
    /*
     * Get NOMBRE de la materia by materia_id que es el id de un prerequisito de una materia..
     */
    function get_codigo_pre_requisito($materia_id)
    {
        $materia = $this->db->query("
            SELECT
                m.materia_codigo

            FROM
                materia m

            WHERE
                `materia_id` = ?
        ",array($materia_id))->row_array();

        return $materia;
    }
}
